import React, { Component } from 'react';
import axios from 'axios';

import Video from './Video'
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';


class App extends Component {
    constructor(props) {
        super(props)
        this.state = {
          videos: [],
          search_items: []
        }
        this.onSearchInputChange=this.onSearchInputChange.bind(this);
     //  this.onSearchInputChange = onSearchInputChange.bind(this);
    }
    componentDidMount(){
    console.log('hidden message from Diana :)')
    // request for best vids
    axios.get('https://api.vimeo.com/channels/bestofthemonth/videos',
          {
            headers: {'Authorization': 'bearer 57930b58a1a128c6c01489efd1ab3163'}
          })
    .then(res => {
          this.setState({

           videos: res.data.data,
           search_items: this.state.videos
          });
    })
    .catch(function (error) {
        console.log(error);
    });
  }
  onSearchInputChange(event){
     console.log("Search changed ..." + event.target.value)
        var updatedList = this.state.videos;
        console.log(this.state.videos)
        updatedList = updatedList.filter(function(item){
          return item.name.toLowerCase().search(
            event.target.value.toLowerCase()) !== -1;
        });

        console.log(updatedList)
        if(updatedList.length!=0){
          this.setState({videos: updatedList});
        }else{
 this.setState({videos: this.state.videos});
        }



        //this.setState({items: updatedList});

        // if (event.target.value) {
        //     this.setState({searchString: event.target.value})
        // } else {
        //     this.setState({searchString: ''})
        // }

  }
  render() {
    return (
      <div>

        { this.state.videos ? (
                    <div>
              <TextField style={{padding: 24}}
                                        id="searchInput"
                                        placeholder="Search for Videos"   
                                        margin="normal"
                                        onChange={this.onSearchInputChange}
                                        />
                        <Grid container spacing={24} style={{padding: 24}}>
                            { this.state.videos.map((currentVid, i)=> (
                                <Grid item key={i} xs={12} sm={6} lg={4} xl={3}>
                                    <Video  video={currentVid} />
                                </Grid>
                            ))}
                        </Grid>
                    </div>
                ) : "No videos found" }
                    </div>

    )
  }
}

export default App;
